tests:
test "analyze positive" {
    let res = analyze_number(156);
    assert(res["original_number"] == 156);
    assert(res["digit_count"] == 3);
    assert(res["is_negative"] == 0);
    assert(res["is_even"] == 1);
}

test "analyze negative" {
    let res = analyze_number(-7);
    assert(res["original_number"] == -7);
    assert(res["digit_count"] == 1);
    assert(res["is_negative"] == 1);
    assert(res["is_even"] == 0);
}

test "analyze zero" {
    let res = analyze_number(0);
    assert(res["original_number"] == 0);
    assert(res["digit_count"] == 1);
    assert(res["is_negative"] == 0);
    assert(res["is_even"] == 1);
}

test "gcd tests" {
    assert(gcd(48, 18) == 6);
    assert(gcd(101, 103) == 1);
    assert(gcd(0, 5) == 5);
    assert(gcd(-4, 14) == 2);
}

test "lcm tests" {
    assert(lcm(4, 6) == 12);
    assert(lcm(21, 6) == 42);
    assert(lcm(-3, 4) == 12);
}

test "sum of squares positive" {
    assert(sum_of_squares(1, 4) == 30); // 1 + 4 + 9 + 16
    assert(sum_of_squares(0, 5) == 55);
}

test "sum of squares negative" {
    assert(sum_of_squares(-2, 2) == 10); // 4 + 1 + 0 + 1 + 4
}

test "sum of squares swapped" {
    assert(sum_of_squares(5, 0) == 0); // start > end, returns 0
}
